// AlimentoDetalle.js

import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const AlimentoDetalle = () => {
    const [alimento, setAlimento] = useState(null);
    const { id } = useParams(); // Obtiene el ID del alimento de la URL

    useEffect(() => {
        fetch(`http://localhost:8000/api/alimentos/${id}/`) // Asegúrate de que la URL sea correcta
            .then(response => response.json())
            .then(data => setAlimento(data))
            .catch(error => console.error('Error:', error));
    }, [id]);

    if (!alimento) {
        return <div>Cargando...</div>;
    }

    return (
        <div>
            <h2>Detalle del Alimento</h2>
            <p><strong>Nombre:</strong> {alimento.nombre}</p>
            <p><strong>Calorías:</strong> {alimento.calorias}</p>
            <p><strong>Proteínas:</strong> {alimento.proteinas}</p>
            <p><strong>Carbohidratos:</strong> {alimento.carbohidratos}</p>
            <p><strong>Grasas:</strong> {alimento.grasas}</p>
            {/* Agrega más detalles según tu modelo Alimento */}
        </div>
    );
};

export default AlimentoDetalle;
